# Service Permission Checker
# Ben Turner @benpturner

<#
.Synopsis
    Service Permission Checker
.DESCRIPTION
	Service Permission Checker
.EXAMPLE
    PS C:\> Get-ServicePerms -Path C:\temp\
#>
$sploaded = $null
Function Get-ServicePerms($path) {

if (!$path){
$path = pwd 
$path = "$path\" 
}

if ($sploaded -ne "TRUE") {
    $script:sploaded = "TRUE"
    echo "Loading Assembly"
    $i = ""
    $dllbytes  = [System.Convert]::FromBase64String($i)
    $assembly = [System.Reflection.Assembly]::Load($dllbytes)
}

[ServicePerms]::dumpservices($path)
$complete = "[+] Writing output to Report-" + $path +"\" + $env:COMPUTERNAME + ".html"
echo "[+] Completed Service Permissions Review"
echo "$complete"

}